(function($){
        var formats = {
                'b': function(val) {return parseInt(val, 10).toString(2);},
                'c': function(val) {return String.fromCharCode(parseInt(val, 10));},
                'd': function(val) {return parseInt(val, 10);},
                'u': function(val) {return Math.abs(val);},
                'f': function(val, p) {
                        p = parseInt(p, 10);
                        val = parseFloat(val);
                        if(isNaN(p && val)) {
                                return NaN;
                        }
